init:
$('body').addClass('textured');
$('.button').pretouch();

// Menu handling functions
if (!OF.menu) {
  OF.menu = function(buttonName) {
    if      (buttonName == 'home')      OF.menu.home();
    else if (buttonName == 'settings')  OF.menu.settings();
    else if (buttonName == 'exit')      OF.menu.exit();
    else {
      OF.log('Unknown menu button hit: '+ buttonName);
    }
  };

  OF.menu.home = function() {
    if (OF.navigationStack[0].url.match(/dashboard\/user/)) {
      OF.goBack({root:true});
    } else {
      OF.navigateToUrl("dashboard/user", {
        complete: function() {
          OF.navigationStack = [OF.page];
          OF.sendAction('back', {root:true});
        }
      });
    }
  };

  OF.menu.settings = function() {
    OF.sendAction('openSettings');
  };

  OF.menu.exit = function() {
    OF.sendAction('dismiss');
  };
}

// Base friends page functions
if (!OF.friendsList) {
  OF.friendsList = {
    init: function() {
      $.lazyLoad({
        threshold: 0.5,  //percentage of the window height left to scroll that will trigger the next page
        nextPageLoaded: function(page, data){ 
          if(page == 1) {
            OF.friendsList.renderFriends(data);                 
          } else {
            OF.friendsList.addFriends(data);         
          }
        },
        nextPageShouldLoad: function(page, callback) {
          OF.api.request('/xp/friends', {
            params: { user_id: OF.page.user.id, page:page, page_size:50 },
            success: function(data){ callback(data.users, data.users.length < 50) },
            failure: function(){ callback([]); }
          });
        },
        isEmpty: function() { OF.friendsList.renderFriends([]); },
        isFinished: function() { $("#loading").hide(); }
      });
    },

    renderFriendItems: function(friends) {
      var html = $.map(friends, function(friend) {
       return tmpl('user_tmpl', friend);
      });
      return html;
    },

    renderFriends : function(friends) {    
      var html = OF.friendsList.renderFriendItems(friends);

      if (html.length > 0) {
        $('#friends_list > ul').html(html.join('')).unhide();
        $("#friends_list .no_data").hide();
      } else {
        $('#friends_list > ul').hide();
        $("#friends_list .no_data").unhide();      
      }
    },

    addFriends : function(friends) {    
      var html = OF.friendsList.renderFriendItems(friends);
      $('#friends_list > ul').append(html.join(''));
    }
  };
}

// Go home on touching the logo area
$('#home_button').touch(OF.menu.home);
var userID = OF.page.params.user_id || OF.user.id;
var gameID = OF.page.params.game_id || OF.game.id;
var leaderboardID = OF.page.params.leaderboard_id;

var name   = OF.page.params.leaderboard_name;
var scoreTmpl = tmpl('score_tmpl');

var everyoneRequest = null;
var myScoreRequest = null;
var friendScoreRequest = null;

// In case the name isn't passed in the params then we fetch it via a request
if (!name) {
  OF.api.request('/xp/games/'+ gameID +'/leaderboards/' + leaderboardID, {
    method: 'GET',
    success: function(data) {
      if (data.leaderboard.name.length > 0) { 
        OF.forceSetTitle(data.leaderboard.name);
      }
    }
  });
} else { OF.page.title = name }

var load = function(interval, tab){
  if(everyoneRequest) everyoneRequest.cancel();
  if(myScoreRequest) myScoreRequest.cancel();
  if(friendScoreRequest) friendScoreRequest.cancel();

  $("section.scores").addClass("hidden");
  $("section.scores ul").remove();
  $("#no_data").addClass("hidden").hide();
  $("#time_frame li").removeClass("active");
  $(tab).addClass("active");
  $(".loading").show();

  var rankScores = function(scores) {
    var rank = 0;
    $.each(scores, function() {
      if (!this.high_score.rank) this.high_score.rank = ++rank;
    });
  }

  var renderScores = function(scoresObject) {
    $.each(scoresObject, function(section, scores) {
      if(scores.length == 0){ return; }

      var html = '';
      $.each(scores, function() {        
        if (this.high_score.display_text && $.trim(this.high_score.display_text).length > 0) {
          this.high_score.display_score = this.high_score.display_text;
        } else {
          this.high_score.display_score = this.high_score.score;
        }

        html += scoreTmpl(this.high_score);
      });

      var ul = $("<ul class='cells scores'>").html(html);
      $('#'+ section).append(ul).unhide();
    });      
  }

  // check to see if we should show the no data screen
  var checkForData = function() {
    allComplete = everyoneRequest.isComplete && myScoreRequest.isComplete && friendScoreRequest.isComplete;
    if (allComplete) {
      //HACK: seems like loader automagic doesn't always work when 
      //concurrent requests share the element.  forcing the hide, for now. 
      $(".loading").hide();

      noData = true;
      $.each(scores, function(section, highScores) {
        if (highScores.length > 0) {
          noData = false;
        }
      });

      if (noData) {
        $("#no_data").unhide();
      } else {
        renderScores(scores);
      }
    }
  }

  var baseParams = interval > 0 ? {interval: interval} : {};

  var scores = {
    everyone: null,
    your_friends: null,
    you: null,
  };

  everyoneRequest = OF.api.request('/xp/games/'+ gameID +'/leaderboards/'+ leaderboardID + '/high_scores', {
    success: function(data) {
      rankScores(data.high_scores);
      scores.everyone = data.high_scores;
    },
    complete: checkForData,
    params: baseParams,
  });

  myScoreRequest = OF.api.request('/xp/users/' + userID + '/games/' + gameID +'/leaderboards/'+ leaderboardID + '/current_score', { 
    success: function(data) { scores.you = [data]; },
    failure: function()     { scores.you = []; },
    complete: checkForData,
    params: baseParams,
  });

  friendScoreRequest = OF.api.request('/xp/games/'+ gameID +'/leaderboards/'+ leaderboardID + '/high_scores', { 
    success: function(data) { 
      rankScores(data.high_scores);

      // if the friend's leaderboard request only contains your score, don't show it
      if (data.high_scores.length == 1 && data.high_scores[0].high_score.user.id == userID) {
        scores.your_friends = [];
      } else {
        scores.your_friends = data.high_scores;
      }
    },
    complete: checkForData,
    params: $.extend(baseParams, {friends_leaderboard: true}),
  });
}

$("#time_frame li").touch(function() {
  if($(this).hasClass("active")){ return; }

  load($(this).attr("data-interval"), $(this))
});

// load(0, $("#time_frame li").first());
load(0, $("#time_frame li").first());
