init:
$('body').addClass('textured');
$('.button').pretouch();

// Menu handling functions
if (!OF.menu) {
  OF.menu = function(buttonName) {
    if      (buttonName == 'home')      OF.menu.home();
    else if (buttonName == 'settings')  OF.menu.settings();
    else if (buttonName == 'exit')      OF.menu.exit();
    else {
      OF.log('Unknown menu button hit: '+ buttonName);
    }
  };

  OF.menu.home = function() {
    if (OF.navigationStack[0].url.match(/dashboard\/user/)) {
      OF.goBack({root:true});
    } else {
      OF.navigateToUrl("dashboard/user", {
        complete: function() {
          OF.navigationStack = [OF.page];
          OF.sendAction('back', {root:true});
        }
      });
    }
  };

  OF.menu.settings = function() {
    OF.sendAction('openSettings');
  };

  OF.menu.exit = function() {
    OF.sendAction('dismiss');
  };
}

// Base friends page functions
if (!OF.friendsList) {
  OF.friendsList = {
    init: function() {
      $.lazyLoad({
        threshold: 0.5,  //percentage of the window height left to scroll that will trigger the next page
        nextPageLoaded: function(page, data){ 
          if(page == 1) {
            OF.friendsList.renderFriends(data);                 
          } else {
            OF.friendsList.addFriends(data);         
          }
        },
        nextPageShouldLoad: function(page, callback) {
          OF.api.request('/xp/friends', {
            params: { user_id: OF.page.user.id, page:page, page_size:50 },
            success: function(data){ callback(data.users, data.users.length < 50) },
            failure: function(){ callback([]); }
          });
        },
        isEmpty: function() { OF.friendsList.renderFriends([]); },
        isFinished: function() { $("#loading").hide(); }
      });
    },

    renderFriendItems: function(friends) {
      var html = $.map(friends, function(friend) {
       return tmpl('user_tmpl', friend);
      });
      return html;
    },

    renderFriends : function(friends) {    
      var html = OF.friendsList.renderFriendItems(friends);

      if (html.length > 0) {
        $('#friends_list > ul').html(html.join('')).unhide();
        $("#friends_list .no_data").hide();
      } else {
        $('#friends_list > ul').hide();
        $("#friends_list .no_data").unhide();      
      }
    },

    addFriends : function(friends) {    
      var html = OF.friendsList.renderFriendItems(friends);
      $('#friends_list > ul').append(html.join(''));
    }
  };
}

// Go home on touching the logo area
$('#home_button').touch(OF.menu.home);
var userID = OF.page.params.user_id || OF.user.id;
var gameID = OF.page.params.game_id || OF.game.id;
var leaderboardTmpl = tmpl('leaderboard_tmpl');

OF.api.request('/xp/games/'+ gameID +'/leaderboards', {
  loader: '.loading',
  success: function(data) {
    var html = '';
    $.each(data.leaderboards, function() {
      html += leaderboardTmpl(this.leaderboard);
    });
    $('#leaderboards_list').html(html).unhide();
  },
  on404: function() {
    $("#leaderboards .no_data").unhide();
    $("#leaderboards_list").hide();
  }
});
