init:
$('body').addClass('textured');
$('.loading').hide();
$('.game_name').html(OF.game.name);

OF.page.bootstrapCallback = function(status, response) {
  $('form').data('isLoading', false);
  if (typeof response === 'string') response = $.parseJSON(response);

  OF.loader.hide();
  if (status.match(/^2/)) {
    OF.log('Successfully bootstrapped with new account');
    OF.sendAction('dismiss');
  } else {
    var message = response.exception.message ? $.htmlDecode(response.exception.message) : 'We had an error processing your information.  Ensure it all looks right and try again.';
    OF.alert('Error', message);
  }
};

$('#submit').touch(function() {
  if (!$('form').data('isLoading')) {
    $('form').data('isLoading', true);
    OF.loader.show();
    OF.sendAction('createUser', {
      name:                   OF.page.params.userName,
      email:                  $('#user_email').val(),
      password:               $('#user_password').val(),
      password_confirmation:  $('#user_password_confirmation').val(),
      callback:               'OF.page.bootstrapCallback'
    });
  }
});

$('form').submit(function() {
  OF.clickElement('#submit');
  return false;
})
