init:
$('body').addClass('textured');
$('#subheader').text(OF.page.title);
$('#submit').touch(function() {
  OF.api.request('/xp/users/reset_password', {
    method: 'PUT',
    loader: '#submit',
    success: function() {
      OF.goBack();
      OF.alert('Success', 'Your password has been reset and emailed to you.');
    }
  });
});
