init:
$('body').addClass('textured');
$('#subheader').text(OF.page.title);
$('#subheader').remove();

OF.page.enableSubscribe = function(enabled) {
  var button = $('#subscribe');

  if (enabled) {
    button.children('span').addClass('subscribed');
  } else {
    button.children('span').removeClass('subscribed');
  }
};

OF.api.request('/xp/news_letter_subscription', {
  success: function(data) {
    OF.page.enableSubscribe(data.news_letter_subscription.is_subscribed);
  }
});

$('#subscribe').touch(function() {
  if (!$('#subscribe').data('isLoading')) {
    $('#subscribe').data('isLoading', true);

    var button = $('#subscribe');
    var isSubscribed = button.children('span').hasClass('subscribed');

    OF.page.enableSubscribe(!isSubscribed);
    OF.api.request('/xp/news_letter_subscription', {
      method: isSubscribed ? 'DELETE' : 'POST',
      success: function() {},
      complete: function() { $('#subscribe').data('isLoading', false) }
    });
  }
});

$('#logout').touch(function() {
  if(confirm('Are you sure you want to logout from Feint?')) {
    OF.sendAction('logout');
  }
});
